package ru.ystu;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import ru.ystu.service.QueueService;class Main {	public static void main(String[] args) {		ApplicationContext context = new ClassPathXmlApplicationContext("beans.xml");		QueueService service = context.getBean(QueueService.class);		service.run();	}}